'use client'

import React, { useState } from "react";
import { motion } from "motion/react";


interface Particle {
  x: number;
  y: number;
  key: number;
}

const ParticleButton: React.FC = () => {
  const [particles, setParticles] = useState<Particle[]>([]);

  const handleClick = (e: React.MouseEvent<HTMLButtonElement>) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const newParticles: Particle[] = Array.from({ length: 10 }).map((_, i) => ({
      x: x + (Math.random() - 0.5) * 20,
      y: y + (Math.random() - 0.5) * 20,
      key: Date.now() + i,
    }));

    setParticles((prevParticles) => [...prevParticles, ...newParticles]);

    // Remove particles after animation ends
    setTimeout(() => {
      setParticles((prevParticles) =>
        prevParticles.filter(
          (p) => !newParticles.some((np) => np.key === p.key)
        )
      );
    }, 500);
  };

  return (
    <motion.button
      initial={{ scale: 1 }}
      whileTap={{ scale: 0.95 }}
      transition={{ type: "spring", stiffness: 300, damping: 20 }}
      className="relative rounded-xl overflow-hidden bg-transparent radial-gradient"
      onClick={handleClick}
    >
      {particles.map((p) => (
        <span
          key={p.key}
          className="absolute rounded-full bg-white opacity-50"
          style={{
            width: 100,
            height: 100,
            left: p.x,
            top: p.y,
            transform: "scale(0)",
            animation: "particle 0.5s forwards",
          }}
        />
      ))}
      <span className="relative px-2 py-2 rounded-xl text-neutral-100 font-light">
        Motion
      </span>
    </motion.button>
  );
};

export default ParticleButton;
